package uk.ac.ed.inf.Serialiser;import com.fasterxml.jackson.core.JsonGenerator;import com.fasterxml.jackson.databind.SerializerProvider;import com.fasterxml.jackson.databind.ser.std.StdSerializer;import uk.ac.ed.inf.ilp.data.Order;import java.io.IOException;public class CustomOrderSerializer extends StdSerializer<Order> {    public CustomOrderSerializer() {        this(null);    }    public CustomOrderSerializer(Class<Order> t) {        super(t);    }    @Override    public void serialize(Order order, JsonGenerator jgen, SerializerProvider provider) throws IOException {        jgen.writeStartObject();        jgen.writeStringField("orderNo", order.getOrderNo());        jgen.writeStringField("orderStatus", order.getOrderStatus().toString());        jgen.writeStringField("orderValidationCode", order.getOrderValidationCode().toString());        jgen.writeNumberField("costInPence", order.getPriceTotalInPence());        jgen.writeEndObject();    }}