package uk.ac.ed.inf;import com.fasterxml.jackson.databind.ObjectMapper;import uk.ac.ed.inf.ilp.data.Order;import java.io.IOException;import java.net.URI;import java.net.MalformedURLException;import java.net.URL;public class TestClient {    public static void main(String[] args){            String baseUrl = "https://ilp-rest.azurewebsites.net";            String orderUrl = baseUrl + "/orders";            ObjectMapper mapper = new ObjectMapper();            try {                URL url = new URL(orderUrl);                URI uri = url.toURI();                Order[] orders = mapper.readValue(uri.toURL(), Order[].class);                System.out.println(orders[0].getOrderNo());            } catch (MalformedURLException e) {                e.printStackTrace();            } catch (IOException e) {                e.printStackTrace();            } catch (Exception e){                e.printStackTrace();            }    }}