package uk.ac.ed.inf;import org.junit.Before;import org.junit.Test;import uk.ac.ed.inf.Client.RestClient;import uk.ac.ed.inf.ilp.data.NamedRegion;import uk.ac.ed.inf.ilp.data.Restaurant;import java.util.ArrayList;public class RestTest {    private RestClient restClient;    @Before    public void setUp(){        restClient = new RestClient("https://ilp-rest.azurewebsites.net");    }    @Test    public void testGetRestaurants(){        Restaurant[] restaurants = restClient.getRestaurants();        if(restaurants.length > 0){            System.out.println("Restaurant endpoint works!");        }    }    @Test    public void testGetCentralArea(){        NamedRegion centralArea = restClient.getCentralArea();        if(centralArea != null){            System.out.println("Central Area endpoint works!");        }    }    @Test    public void testGetNoFlyZones(){        ArrayList<NamedRegion> noFlyZones = new ArrayList<>(restClient.getNoFlyZones());        if(noFlyZones.size() > 0){            System.out.println("No Fly Zones endpoint works!");        }    }    @Test    public void testGetOrders(){        if(restClient.getOrders().size() > 0){            System.out.println("Orders general endpoint works!");        }    }    @Test    public void testGetOrdersForDate(){        if (restClient.getOrders("2023-12-03").size() > 0){            System.out.println("Orders for date endpoint works!");        }    }    @Test    public void testTime(){        App.main(new String[]{"2023-12-06","https://ilp-rest.azurewebsites.net"});        System.out.println("Everything Works! Chill!");    }}