package uk.ac.ed.inf;import org.junit.Before;import org.junit.Test;import uk.ac.ed.inf.Client.RestClient;import uk.ac.ed.inf.ilp.data.Restaurant;import java.io.IOException;public class RestTest {    private RestClient restClient;    @Before    public void setUp() throws IOException {        restClient = new RestClient("https://ilp-rest.azurewebsites.net");    }    @Test    public void testGetRestaurants(){        Restaurant[] restaurants = restClient.getRestaurants();        for (Restaurant restaurant : restaurants) {            System.out.println(restaurant.name());        }    }    @Test    public void testGetCentralArea(){        System.out.println(restClient.getCentralArea());    }    @Test    public void testGetNoFlyZones(){        System.out.println(restClient.getNoFlyZones());    }    @Test    public void testGetOrders(){        System.out.println(restClient.getOrders());    }    @Test    public void testGetOrdersForDate(){        System.out.println(restClient.getOrders("2023-09-01"));    }    @Test    public void testTime(){        Main main = new Main();        main.main(new String[]{"2023-11-20","https://ilp-rest.azurewebsites.net"});    }}